Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing

#Custom Icon
$IconBase64 = ""
$IconBytes = [Convert]::FromBase64String($IconBase64)
$ims = New-Object IO.MemoryStream($IconBytes, 0, $IconBytes.Length)
$ims.Write($IconBytes, 0, $IconBytes.Length)

# Create a form
$form = New-Object System.Windows.Forms.Form
$form.Text = "Random Dad Joke"
$form.Size = New-Object System.Drawing.Size(305, 320)
$form.StartPosition = "CenterScreen"
$form.FormBorderStyle = "FixedDialog"
$form.Icon = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $ims).GetHIcon())
$form.TopMost = $true

$button1 = New-Object System.Windows.Forms.Button
$button1.Size = New-Object System.Drawing.Size(125, 166)
$button1.Location = New-Object System.Drawing.Point(83, 25)
$IconBytes = [Convert]::FromBase64String($IconBase64)
$Stream = [System.IO.MemoryStream]::new($IconBytes, 0, $IconBytes.Length)
$button1_Picture = [System.Drawing.Icon]::FromHandle(([System.Drawing.Bitmap]::new($Stream).GetHIcon()))
$button1.Image = $button1_Picture
$button1.add_Click({
    Dadjoke
  })
$form.Controls.Add($button1)

$label1 = New-Object System.Windows.Forms.Label
$label1.Text = "Random Dad Joke"
$label1.AutoSize = $true
$label1.Location = New-Object System.Drawing.Point(100, 200)
$form.Controls.Add($label1)

$button2 = New-Object System.Windows.Forms.Button
$button2.Size = New-Object System.Drawing.Size(125, 30)
$button2.Text = "Close"
$button2.Location = New-Object System.Drawing.Point(83, 235)
$button2.add_Click({
    $form.Dispose()
  })
$form.Controls.Add($button2)

function Dadjoke {
  $DadJoke = Invoke-RestMethod -Uri https://icanhazdadjoke.com/ -Headers @{accept = "text/plain" }
  $xml = @"
<toast>
  
  <visual>
    <binding template="ToastGeneric">
      <text>Random Dad Joke</text>
      <text>$DadJoke</text>
    </binding>
  </visual>

  <audio silent="true"/>
  
</toast>
"@
  $XmlDocument = [Windows.Data.Xml.Dom.XmlDocument, Windows.Data.Xml.Dom.XmlDocument, ContentType = WindowsRuntime]::New()
  $XmlDocument.loadXml($xml)
  $AppId = '{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\WindowsPowerShell\v1.0\powershell.exe'
  [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]::CreateToastNotifier($AppId).Show($XmlDocument)

  #Male
  Add-Type -AssemblyName System.speech
([System.Speech.Synthesis.SpeechSynthesizer]::New()).Speak($DadJoke)
}

# Make PowerShell Disappear
$windowcode = '[DllImport("user32.dll")] public static extern bool ShowWindowAsync(IntPtr hWnd, int nCmdShow);'
$asyncwindow = Add-Type -MemberDefinition $windowcode -Name Win32ShowWindowAsync -Namespace Win32Functions -PassThru
$null = $asyncwindow::ShowWindowAsync((Get-Process -PID $pid).MainWindowHandle, 0)

# Show the form
$form.ShowDialog() | Out-Null